tinymce.PluginManager.add('prism', function(editor, url) {
    
    var languageList = [
        'Markup',
        'CSS',
        'Javascript',
        'PHP',
        'CoffeeScript',
        'Sass',
        'Bash',
        'SQL',
        'HTTP',
        'Ruby',
        'Ini',
        'Apache',
        'Git',
        'Less',
        'Markdown'
    ];

    var menuLanguages = [];
    tinymce.each(languageList, function(language) {
        menuLanguages.push({
            text: language,
            onclick: function() {
                editor.insertContent('<pre class="language-' + language.toLowerCase() + '"</pre>');
            }
        });
    });

    editor.addButton('prism', {
        text: 'Languages',
        type: 'menubutton',
        icon: false,
        menu: menuLanguages
    });

});